import Foundation

func day6() {
    
    let datastream = Array(day6PuzzleInput)
    var markerIndex: Int = 0

    for index in stride(from: 4, to: datastream.count - 1, by: 1) {
        
        // method 1: Almond Peko way
        
        let charactersToCheck = datastream[(index-4)..<index].sorted()
        var isStartMarker = true

        for charIndex in 0..<3 {
            if charactersToCheck[charIndex] == charactersToCheck[charIndex + 1] {
                isStartMarker = false
                break
            }
        }

        if isStartMarker {
            markerIndex = index
            break
        }
        
        // method 2: using set
        let characterSet: Set<Character> = Set(datastream[(index-4)..<index])
       
        if characterSet.count == 4 {
            markerIndex = index
            break
        }
    }
    
    print(markerIndex)
    
    //PART II
    
    var messageIndex = 0
    
    for index in stride(from: 14, to: datastream.count - 1, by: 1) {
        let charactersToCheck = datastream[(index-14)..<index].sorted()
        var isStartMarker = true
        
        for charIndex in 0..<13 {
            if charactersToCheck[charIndex] == charactersToCheck[charIndex + 1] {
                isStartMarker = false
                break
            }
        }
        
        if isStartMarker {
            messageIndex = index
            break
        }
    }
    
    print(messageIndex)
    
}


/*
 easier method is to :
 1. create an array of 4 characters to check
 2. create a set from the array
 3. if set.count == array.count -> no duplicates present. Recall that set doesn't allow duplicates
